    public static int reversePairs(int[] nums) {
        int n = nums.length;
        int count = 0;
        for (int i = 0; i < n; i++) {

            for (int j = i+1; j < n; j++) {
//                long no = 2L * nums[j];
                long no = (2L * nums[j]);
                if((long)(nums[i])  > no){
                    count++;
                }
            }
        }
        return count;
    }

